INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('101', 'Kramer’s Kibbles', '24.99', '300', 'From the hit TV Show Seinfeld comes Kramer’s Kibbles! This fantastic chow has all of the nutrients your dog needs so that he won’t have to go next door for a snack!', 'product-images/kramer\'s-kibbles.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('102', 'All Green Dog Food', '39.99', '400', 'This all organic dog chow is made from all natural ingredients. It\'s so green that your dog may turn green! *Only feed in specified doses.', 'product-images/All-Green-Dog-Food.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('103', 'Bone Dry Food', '9.99', '200', 'This chow is for the God fearing dog that doesn’t mind things the old fashioned way. No special ingredients to be seen here.', 'product-images/bone-dry-food.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('104', 'Better Than Human Food', '34.99', '400', 'For the family where enough is never enough. Serve your mutt michelin star meals because you wouldn’t have it any other way. During the nuclear winters you can even pop open a can to feed yourself!', 'product-images/better-than-human-food.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('105', 'Sigma Wolf Chow', '29.99', '350', 'Feed the inner beast inside your pup that has been hankering for some genuine red meat. Let your pup rip and tear at the most processed meats to be fit into kibble shape.', 'product-images/sigma-wolf-chow.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('106', 'Jerry Seinfeld Dog Toy', '8.99', '500', 'From the hit TV Show Seinfeld comes Jerry Seinfeld Dog Toy. What’s the deal with dog toys? For the dog that just can’t stop binging Seinfeld.', 'product-images/Jerry-Seinfeld-Dog-Toy.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('107', 'Annoying Chicken', '9.99', '200', 'Who needs sleep anyway? This chicken squeaker will keep your dog busy all throughout the night. Such fun! (French Bulldog not included).', 'product-images/annoying-chicken.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('108', 'Dog Dog Toy', '17.99', '450', 'Is your dog lonely? Get them a Dog Dog Toy. No you won’t have to give your pup any attention because they’ve got their own friend to pass the time with.', 'product-images/Dog-Dog-Toy.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('109', 'Official Bush’s Beans Dog Toy Set', '24.99', '300', 'The Official Bush’s Beans Dog Toy Set includes the Bush’s Baked Beans toy along with the limited edition Bush’s Chili Beans Kidney Beans Mild Chili Sauce toy.', 'product-images/Beans-Dog-Toy.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('110', 'Dog Whiskey', '19.99', '400', 'Some might ask why a dog would need a whiskey bottle to play with but they clearly don’t understand things like you do. Buy today!', 'product-images/Whiskey-Dog-Toy.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('111', 'Dog Collar', '8.99', '400', 'Every dog needs a collar and every collar needs a dog. We hope that today you can give a collar a nice home around your pup’s neck.', 'product-images/dog-collar.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('112', 'Spikey Dog Collar', '14.99', '200', 'For the dog who is too cool for school and a normal collar would be selling them short. Get them this spikey collar that will tell every pedestrian to keep their distance from this beast.', 'product-images/spikey-collar.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('113', '“Good Boy” Bandana', '9.99', '450', 'You can’t have a good boy without a nice bandana around its neck. Perfect for the honest God-fearing dog.', 'product-images/dog-bandana.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('114', 'Dog Jordans', '54.99', '100', 'Officially licensed Dog Jordans and Lebron doggy Jersey. For the hype beast that is in every dog. ', 'product-images/dog-jordans.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('115', 'Doggy Drip', '89.99', '1', 'Does your dog radiate swagger and charisma with every step? Get them this genuine gold chain to show off and assert their innate superiority over other dogs.', 'product-images/doggy-drip.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('116', 'Super Duper Pooper Scooper', '19.99', '100', 'Every dog needs to poop and every poop needs to be scooped. This is where the Doggy Pooper Scooper comes in. This makes it so easy for humans to collect their dogs poop.', 'product-images/doggy-pooper-scooper.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('117', 'Dog Leash', '14.99', '300', 'Does your dog like to walk in order to escape being trapped inside all day? Don’t let them be too free and ensure they understand their captivity with a rope that stops them from truly being a dog.', 'product-images/dog-leash.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('118', '“Good Boy” Harness', '17.99', '350', 'Keep your dog thoroughly strapped down with this “Good Boy” Harness because every dog needs a conveniently accessible handle.', 'product-images/good-boy-harness.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('119', 'Overpriced Dog Bowl', '39.99', '200', 'Do you want to keep your dog’s food and water at the temperature that it currently is for a little bit longer than it would be otherwise. Then spend twice the amount of money on this product.', 'product-images/over-priced-dog-bowl.jpeg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('120', '“Good Boy” Dog Bed', '29.99', '50', 'Sleepy puppy needs a sleepy bed. Give your dog this comfy bed but buyer beware your doggy may never want to leave it.', 'product-images/dog-bed.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('121', 'Kramer’s Kitty Delight', '24.99', '30', 'Fans of “Kramer’s Kibbles” will be delighted to know that Kramer makes food for cats too. Your cat will kick open the door as quickly as possible to get at this yummy food.', 'product-images/kramer\'s-kitty-delight.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('122', 'Bone Dry Cat Food', '9.99', '100', 'Cat’s don’t need actual good food. Instead get them some really dry smelly food. Flavor? Who needs it?', 'product-images/bone-dry-cat-food.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('123', 'Wet Food in a Can', '14.99', '200', 'Have you ever wished for a bunch of really small cans holding paste that your cats really love? That’s a really lame wish but you can buy it if you really want to.', 'product-images/wet-food-in-a-can.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('124', 'Cat Food With Peas', '18.99', '200', 'Introducing: Cat Food With Peas! Humans don’t want to eat peas so we put it in cat food. Please buy this. We have too many peas and we need to get rid of them somehow.', 'product-images/cat-food-with-peas.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('125', 'Alpha Panther Food', '19.99', '100', 'You need to feed the inner panther inside of your house cat. Let them remember the great outdoors that their ancestors fought through with this carnivore delight.', 'product-images/alpha-panther-food.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('126', 'Cat Tank', '44.99', '100', 'Sometimes a cat just needs to go to war and fight for what is right. This is for the militant cat that we all have in our lives. This product features kevlar plating.', 'product-images/cat-tank.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('127', 'Really Tiny Mouse', '1.99', '800', 'Have you ever wanted a really teeny mouse for your cat to play with for a few days and inevitably lose. Here you go!', 'product-images/really-small-mouse.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('128', 'Sisyphus Cat', '97.99', '0', 'For the cat who is burdened with the plight of man. An eternal struggle. For the cat cursed by the gods to a life of never-ending struggle and hardship.', 'product-images/sisyphus-cat.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('129', 'Cat-Puter', '29.99', '300', 'Every cat needs to get their software engineering classwork done. This Cat-Puter works with SQL but your cat will still have trouble with the query statements just like everyone else.', 'product-images/cat-puter.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('130', 'Cat Laser-Matic', '95.99', '250', 'Have you ever wanted a robot in your house with a laser? Buy a laser robot for your house.', 'product-images/cat-laser.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('131', 'Cat Collar', '8.99', '350', 'Every cute kitty needs a little bell around its neck to give the birds in the backyard a fighting chance.', 'product-images/cat-collar.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('132', 'Cat Bow Tie', '11.99', '400', 'For the formal cat who goes on dinner dates on the weekends. Give them this fancy little tie because they are such a good kitty.', 'product-images/cat-bow-tie.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('133', 'Kitty Hat', '9.99', '300', 'Ever wanted to burden your cat with something they definitely don’t want on their head. The kitty hat is the purrfect tool to turn your cat into an enemy.', 'product-images/kitty-hat.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('134', 'Cat Lion Costume', '14.99', '240', 'The kitty thinks that they look so scary in their lion costume but really they are so adorable. This cat can never be taken seriously.', 'product-images/cat-lion.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('135', 'Kitty Drip', '89.99', '100', 'Does your cat have too much clout to handle? Get them some drip so that they can swag out on the other kitties.', 'product-images/swag-kitty-color.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('136', 'Kitty Bed', '14.99', '300', 'So soft and fluffy. This kitty bed will give your cat the perfect place to stay, however, most cats will refuse to sleep at night and instead cause trouble at 3 am.', 'product-images/cat-bed.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('137', 'Cat Crack', '24.99', '250', 'Is your cat a total junky? Fuel their addiction with cat crack. Because what is life without dependency!', 'product-images/cat-nip.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('138', 'Cat Bowls Set', '19.99', '200', 'Are you tired of your cat having to eat their food out of bowls that don’t look like their face? Get bowls that look like your cat\'s face!', 'product-images/cat-bowls.png');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('139', 'Cat-Tropolis', '98.99', '40', 'This sprawling maze of cat fun will take up way too much space in your living room and the cat will never use it. But you will never get rid of it because you spent too much on it to just give it up.', 'product-images/cat-tropolis.jpg');
INSERT INTO `supawdb`.`product` (`productID`, `productName`, `price`, `inventory`, `description`, `imagePath`) VALUES ('140', 'Kitty Bread Bed', '17.99', '400', 'Ever wanted a nice bread loaf for your cat to sleep on? That’s a strange thing to ask for but here it is!', 'product-images/bread-cat-bed.jpg');
